import { useState } from "react"
import Chai from "./Chai"
import Count from "../src/components/Counter.js"

function App() {
  const arr = ["mango", "orange", "lemon "]
  const [count, setCount] = useState(0);
  let i = 0;
  const increase = () => {
    setCount(i += 1)
  }

  const decrease = () => {
    setCount(count - 1)
  }

  function stopForward(){
    setCount(0)
  }

  function stopBackward(){
    setCount(arr.length-1)
  }

  return (
    <>
    <Chai />
    <button onClick={count < arr.length-1 ? increase: stopForward}>increase</button>
    <button onClick={count > 0 ? decrease: stopBackward}>decrease</button>
    <p>{arr[count]}</p>
    <Count style={{backgroundColor: red, color: white}} />
    </>
  );
}

export default App;
