import { useState } from "react";

const colors = {
	color1: "red",
	color2: "yellow"
}

function BgChanger() {
	const [color, setColor] = useState("red");
	const [bgcolor, setbgColor] = useState("black");

	let isColor = false;

	function colorChanger() {
		if (isColor) {
			setColor("blue")
		} else {
			setColor(color)
		}
		isColor = !isColor;
	}

	function backgroundChanger() {
		if (isColor){
           setbgColor("")
		}
	}

	return (
		<div>
		<button onClick={colorChanger}>changeColor</button>
         <h1 style={{color: color}}>Background Color Changer</h1>
		</div>
 )
}

export default BgChanger
