	let isColor = false;

	function colorChanger() {
		setColor("blue")
	}

    isColor = !isColor;
	return (
		<div>
		<button onClick={colorChanger}>changeColor</button>
         <h1 style={{color: isColor ? "red" : color}}>Background Color Changer</h1>
		</div>
 )
}

export default BgChanger
