import { useState } from "react"
import Chai from "./Chai"

function App() {
  const [count, setCount] = useState(1);

  const increase = () => {
    setCount(count + 1)
  }

  const decrease = () => {
    setCount(count - 1)
  }

  function stopForward(){
    setCount(count = 0)
  }

  function stopBackward(){
    setCount(count = count)
  }

  return (
    <>
    <Chai />
    <button onClick={count < 10 ? increase: stopForward}>increase</button>
    <button onClick={count > 1 ? decrease: stopBackward}>decrease</button>
    <p>{count}</p>
    </>
  );
}

export default App;
