import { useState } from "react"
import Chai from "./Chai"

function App() {
  const [count, setCount] = useState(0);

  const increase = () => {
    setCount(count + 1)
  }

  const decrease = () => {
    setCount(count - 1)
  }

  function stop(){
    setCount(count)
  }

  return (
    <>
    <Chai />
    <button onClick={count <= 10 ? increase: stop}>increase</button>
    <button onClick={decrease}>increase</button>
    <p>{count}</p>
    </>
  );
}

export default App;
