import { useState } from "react";

function Counter(){
	const [count, setCount] = useState(0);

	const increment = () => {
		setCount(count + 1);
	}

	const decrement = () => {
		setCount(count - 1);
	}



	return (
		<div style={{backgroundColor: "red", color: "white"}}>
         <button onClick={count < 10 ? increment : setCount(0)}>Increment Value</button>
         <button onClick={count > 0 ? decrement : setCount(10)}>Increment Value</button>
         <p>{count}</p>
		</div>
)
}

export default Counter;
