import { useState } from "react"
import Chai from "./Chai"

function App() {
  const {count, setCount} = useState(0);

  function counter(){
    setCount(count + 1)
  }
  return (
    <>
    <Chai />
    <button onClick={counter()}>increase</button>
    <p>{count}</p>
    </>
  );
}

export default App;
