import { useState } from "react";

const colors = {
	color1: "red",
	color2: "yellow"
}

function BgChanger() {
	const [color, setColor] = useState("");
	const [bgcolor, setbgColor] = useState("black");

	let isColor = true;

	function colorChanger() {
		if (isColor) {
			setColor("yellow")
		} else {
			setColor("blue")
		}
		isColor = !isColor;
	}

	function backgroundChanger() {
		if (isColor){
           setbgColor("red")
		} else {
			setbgColor("blue")
		}
		isColor = !isColor
	}

	return (
		<div style={{backgroundColor: bgcolor}}>
		<button onClick={colorChanger}>changeColor</button>
         <h1 style={{color: color}}>Background Color Changer</h1>
		</div>
 )
}

export default BgChanger
