import { useState } from "react";

function Counter(){
	const [count, setCount] = useState(0);

	const increment = () => {
		setCount(count + 1);
	} 

	return (
		<div>
         <button onClick={increment}>Increment Value</button>
         <button>Decrement Value</button>
         <p>{count}</p>
		</div>
)
}

export default Counter;
