import { useState } from "react";

const colors = {
	color1: "red",
	color2: "yellow"
}

function BgChanger() {
	const [color, setColor] = useState(true);
	const [bg, setBg] = useState(true);

	function colorChanger() {
		setColor(!color)
	}

    function bgColor(){
    	setBg(!bg)
    }
	return (
		<div style={{backgroundColor: color ? "black": "green"}}>
		<button onClick={colorChanger}>changeColor</button>
		<button onClick={bgColor}>changeColor</button>
         <h1 style={{color: color ? "blue" : "red"}}>Background Color Changer</h1>
		</div>
 )
}

export default BgChanger
