import React from "react";


const Card = (props) => {
    return (
     <div className="bg-light-black dib br3 pa3 ma2 grow shadow-5 ">
        <img alt="robots" src={`https://robohash.org/set_set4/bgset_bg1/${props.id}?size=200x200`} />
     	<div>
     		<h2 className="bg-light-blue br2 pa1 i tc white grow">{props.name}</h2>
     		<p className="bg-green tc white b pa2">{props.greet}</p>
     	</div>
     </div>
	)
}

export default Card;
