import { useState } from "react"
import Chai from "./Chai"

function App() {
  const arr = ["mango", "orange", "lemon "]
  const [count, setCount] = useState(0);
  const [arrFruit, setFruit] = useState(arr[0]);

  const increase = () => {
    // setCount(count + 1)
    setFruit(arrFruit + 1);
  }

  const decrease = () => {
    setCount(count++)
  }

  function stopForward(){
    setCount(count)
  }

  function stopBackward(){
    setCount(count--)
  }

  return (
    <>
    <Chai />
    <button onClick={count < arr.length ? increase: stopForward}>increase</button>
    <button onClick={count > 1 ? decrease: stopBackward}>decrease</button>
    <p>{count}</p>
    </>
  );
}

export default App;
